const Product = ({ product }) => {

  const {cart} = useSelector((state) => state);


  return(
    <div>
      <div>
        <p>{product.title}</p>
      </div>
      <div>
        <p>{product.description}</p>
      </div>
      <div>
        <img src={product.image} alt={product.title} />
      </div>
      <div>
        <p>${product.price} </p>
      </div>
      {
        cart.some((p) => p.id === product.id) ?
        (<button>
          Remove from Cart
        </button>) :
        (<button>
          Add to Cart
        </button>) 
      }

      </div>
  )
};

export default Product;
